import { useState } from "react";

const initialItems = [
  { id: 1, description: "Passports", quantity: 2, packed: true },
  { id: 2, description: "Socks", quantity: 12, packed: false },
];

function App() {
  const [its, setIts] = useState([]);
  function add(item) {
    setIts((its) => [...its, item]);
  }
  function handleDelete(id) {
    setIts((its) => its.filter((item) => item.id !== id));
  }
  return (
    <div className="app">
      <Logo />
      <Form add={add} />
      <PackingList its={its} handleDelete={handleDelete} />
      <Stats />
    </div>
  );
}
function Logo() {
  return <h1>üå¥ Far Away üíº</h1>;
}

function Form({ add }) {
  const [des, SetDes] = useState("");
  const [q, SetQ] = useState(1);
  function handlerSubmit(e) {
    e.preventDefault();

    add({ q, des, id: new Date() });
    SetDes("");
    SetQ(1);
  }
  return (
    <form className="add-form" onSubmit={handlerSubmit}>
      <h3>what do u want for trip</h3>
      <select value={q} onChange={(e) => SetQ(e.target.value)}>
        {Array.from({ length: 20 }, (_, i) => i + 1).map((op) => (
          <option value={op} key={op}>
            {op}
          </option>
        ))}
      </select>
      <input
        type="text"
        placeholder="item"
        value={des}
        onChange={(e) => {
          SetDes(e.target.value);
        }}
      ></input>
      <button>add</button>
    </form>
  );
}
function PackingList({ its, handleDelete }) {
  return (
    <div className="list">
      <ul>
        {its.map((item) => (
          <Item item={item} key={item.id} handleDelete={handleDelete} />
        ))}
      </ul>
    </div>
  );
}
function Item({ item, handleDelete }) {
  return (
    <li>
      <span style={item.packed ? { textDecoration: "line-through" } : {}}>
        {item.q}
        {item.des}
      </span>
      <button onClick={() => handleDelete(item.id)}>‚ùå&times;</button>
    </li>
  );
}
function Stats() {
  return (
    <footer className="stats">
      <em>You have X item in your list ,you have packed X items(X)</em>
    </footer>
  );
}
export default App;
